        clip-path: circle(0% at 784px 93%);


        .c-button {
            background-color: $webfatorial-white;
            color: $text-color;

            &:hover {
                background-color: darken($webfatorial-white, 15%);
            }
        }
    }

    &__title {
        font-weight: normal;
        line-height: 1;
        margin: 0;

        &-emphasis {
            display: inline-block;
            flex: 1;
            font-size: 210%;
            font-weight: bold;
            width: 100%;
        }
    }

    &__text {
        line-height: 1.6;
        margin: $global-module-size 0;
    }
}

